from flask import Flask, render_template

app = Flask(__name__)

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/vupsen')
def vupsen_page():
    link = "data:image/jpeg;base64,"
    description = "Несмотря на свой характер, Вупсеню не чужды страхи. В серии «Трусишка Вупсень», он не может спрыгнуть с батута, потому что боится высоты. В серии «Обманщики», Вупсень по этой же причине не соглашается на шутку с обрывом."
    return render_template('actor.html', name="Вупсень", description=description, img=link)

@app.route('/pupsen')
def pupsen_page():
    link = "data:image/jpeg;base64,"
    description = "Он один из двух неразлучных братьев-близнецов гусениц. Пупсень – мягче и лояльнее своего напарника по прогулам в школе и хулиганствам."
    return render_template('actor.html', name="Пупсень", description=description, img=link)

@app.route('/luntik')
def luntik_page():
    link = "data:image/jpeg;base64, "
    description = "Лунтик самый добродушный и вежливый житель лесной полянки. Он всегда готов прийти на помощь к тому, кто в ней нуждается, дать совет, прислушаться к чужому мнению и старается никого не обидеть и не причинить зла."
    return render_template('actor.html', name="Лунтик", description=description, img=link)

@app.route('/kuzya')
def kuzya_page():
    link = "data:image/png;base64,"
    description = "Кузя очень любопытен, деятелен, отличается редкостным жизнелюбием, но в то же время он – хвастун, выдумщик и очень любит лесть."
    return render_template('actor.html', name="Кузя", description=description, img=link)

app.run(debug=True)